ValidationRule.errorConditionFormula:
OR( AND( $Profile.Name <> 'Administrador del sistema', OR(ISPICKVAL(Direccion_Operaciones__c, 'IT&DS'),ISPICKVAL(Direccion_Operaciones__c, 'INNOVATION')), ISBLANK(Codigo_OC__c), OR( ISBLANK(TEXT(Account.Sector__c)), ISBLANK(TEXT(Account.Subsector__c)) ) ),
AND( $Profile.Name <> 'Administrador del sistema', ISPICKVAL(Direccion_Operaciones__c,'ENGINEERING'), ISBLANK(Codigo_OC__c), ISBLANK(TEXT(Account.Sector__c))
)
)
